import React, {useState} from "react";

const Settings = () => {
// -----------------Variable Declaration for Form---------------------------
    const [title, setTitle] = useState('');
    const [location, setLocation] = useState('');
    const [description, setDescription] = useState('');
    const [price, setPrice] = useState('');
    const [willDeliver, setWillDeliver] = useState(false)
// 

    return(
        <div>
            {localStorage.getItem('token') && localStorage.getItem('token').length ?

                <div>
                    <p>Account information</p>



                    <p>Make a Listing!</p>
                    <form>
                        <label>What are you trying to sell?</label>
                        <input type= 'text' value= {title}></input>

                        <label>Location:</label>
                        <input type= 'text' value= {location}></input>

                        <br/>

                        <label>Description:</label>
                        <input type= 'text' value= {description}></input>

                        <br/>

                        <label>Price:</label>
                        <input type= 'text' value= {price}></input>

                        <label>Are you able to deliver this item?</label>
                        <input type='checkbox' id= 'delivery' value= {willDeliver}></input>
                        <button type= 'submit'>Submit Posting</button>
                    </form>
                </div>

                :<p>Please Log in first.</p>
            }
            </div>

)}

export default Settings
