import React from 'react';
import { useOutletContext, useParams } from 'react-router-dom';

const Postings = () => {

    const posts = useOutletContext();
    console.log('We are in the post component: ', posts)

    const { id } = useParams();

    console.log('this is the params object: ', useParams())
    console.log('this is our parameter: ', id)

    const specificPost = posts[id];

    return(
        <div>
            {
                specificPost.title && specificPost.title.length ? <p>{specificPost.title}</p>
                :
                <p>No posts to display</p>
            }
        </div>
    )
}

export default Postings
